-- 게시판 테이블 생성
create table board(
board_no number primary key,
board_head char(6) check(board_head in ('정보','공지','유머')),
board_title varchar2(300) not null,
board_writer references member(member_id) on delete set null,
board_content varchar2(4000) not null,
board_date date default sysdate not null,
board_read number default 0 not null
);

-- 게시판 시퀀스 생성
create sequence board_seq nocache;

-- 댓글 개수 추가
ALTER TABLE board MODIFY board_replycount DEFAULT 0 NOT NULL;

-- 계층형 게시판 항목 추가
-- 상위글(super_no), 그룹번호(group_no), 차수(depth)
-- 데이터가 없어야 추가가 가능
ALTER TABLE board ADD super_no NOT NULL REFERENCES board(board_no) ON DELETE CASCADE;
ALTER TABLE board ADD group_no NUMBER NOT NULL;
ALTER TABLE board ADD depth NUMBER NOT NULL;

-- 페이징 테스트를 위한 샘플 데이터 등록

COMMIT;

update board set group_no=board_no
where depth=0;

